import{_ as a,c as l,o as r,a3 as t}from"./chunks/framework.CF225qYA.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{"sidebar":false},"headers":[],"relativePath":"history/index.md","filePath":"history/index.md","lastUpdated":null}'),o={name:"history/index.md"},i=t('<h3 id="中国朝代及重要事件" tabindex="-1">中国朝代及重要事件 <a class="header-anchor" href="#中国朝代及重要事件" aria-label="Permalink to &quot;中国朝代及重要事件&quot;">​</a></h3><br><h4 id="_1-传说时期-约前-26-世纪-前-22-世纪初" tabindex="-1">1. <a href="/history/1">传说时期（约前 26 世纪-前 22 世纪初）</a> <a class="header-anchor" href="#_1-传说时期-约前-26-世纪-前-22-世纪初" aria-label="Permalink to &quot;1. [传说时期（约前 26 世纪-前 22 世纪初）](/history/1)&quot;">​</a></h4><ul><li><strong>五帝</strong>：黄帝、颛顼、帝喾、尧、舜。这一时期以部落联盟的形式存在，通过禅让制传递首领之位。</li></ul><h4 id="_2-夏朝-约前-21-世纪-前-16-世纪" tabindex="-1">2. 夏朝（约前 21 世纪-前 16 世纪） <a class="header-anchor" href="#_2-夏朝-约前-21-世纪-前-16-世纪" aria-label="Permalink to &quot;2. 夏朝（约前 21 世纪-前 16 世纪）&quot;">​</a></h4><ul><li><strong>建立</strong>：大禹治水有功，被选举为天子，建立夏朝。</li><li><strong>重要事件</strong>：启夺取帝位，标志着世袭制代替禅让制；少康中兴，复辟夏朝。</li></ul><h4 id="_3-商朝-约前-16-世纪-前-1046-年" tabindex="-1">3. 商朝（约前 16 世纪-前 1046 年） <a class="header-anchor" href="#_3-商朝-约前-16-世纪-前-1046-年" aria-label="Permalink to &quot;3. 商朝（约前 16 世纪-前 1046 年）&quot;">​</a></h4><ul><li><strong>建立</strong>：鸣条之战后，商汤灭夏，建立商朝。</li><li><strong>重要事件</strong>：武丁盛世，国力鼎盛；纣王无道，牧野之战后商朝灭亡。</li></ul><h4 id="_4-西周-前-1046-年-前-771-年" tabindex="-1">4. 西周（前 1046 年-前 771 年） <a class="header-anchor" href="#_4-西周-前-1046-年-前-771-年" aria-label="Permalink to &quot;4. 西周（前 1046 年-前 771 年）&quot;">​</a></h4><ul><li><strong>建立</strong>：周武王伐纣，建立周朝，定都镐京。</li><li><strong>重要事件</strong>：分封制、宗法制的确立；周幽王烽火戏诸侯，导致西周灭亡。</li></ul><h4 id="_5-东周-前-770-年-前-256-年" tabindex="-1">5. 东周（前 770 年-前 256 年） <a class="header-anchor" href="#_5-东周-前-770-年-前-256-年" aria-label="Permalink to &quot;5. 东周（前 770 年-前 256 年）&quot;">​</a></h4><ul><li><strong>春秋</strong>（前 770 年-前 476 年）：诸侯争霸，孔子创立儒家学派。</li><li><strong>战国</strong>（前 475 年-前 221 年）：七雄并立，商鞅变法，百家争鸣。</li></ul><h4 id="_6-秦朝-前-221-年-前-206-年" tabindex="-1">6. 秦朝（前 221 年-前 206 年） <a class="header-anchor" href="#_6-秦朝-前-221-年-前-206-年" aria-label="Permalink to &quot;6. 秦朝（前 221 年-前 206 年）&quot;">​</a></h4><ul><li><strong>建立</strong>：秦始皇统一六国，建立秦朝。</li><li><strong>重要事件</strong>：统一度量衡、货币、文字；焚书坑儒；陈胜吴广起义；秦朝灭亡。</li></ul><h4 id="_7-汉朝-前-202-年-220-年" tabindex="-1">7. 汉朝（前 202 年-220 年） <a class="header-anchor" href="#_7-汉朝-前-202-年-220-年" aria-label="Permalink to &quot;7. 汉朝（前 202 年-220 年）&quot;">​</a></h4><ul><li><strong>西汉</strong>（前 202 年-8 年）：刘邦建立西汉，文景之治，汉武帝开疆拓土。</li><li><strong>东汉</strong>（25 年-220 年）：刘秀建立东汉，光武中兴，黄巾起义。</li></ul><h4 id="_8-三国时期-220-年-280-年" tabindex="-1">8. 三国时期（220 年-280 年） <a class="header-anchor" href="#_8-三国时期-220-年-280-年" aria-label="Permalink to &quot;8. 三国时期（220 年-280 年）&quot;">​</a></h4><ul><li><strong>重要事件</strong>：曹丕代汉建魏，刘备建立蜀汉，孙权建立东吴；三国鼎立局面形成；西晋灭吴，统一全国。</li></ul><h4 id="_9-晋朝-265-年-420-年" tabindex="-1">9. 晋朝（265 年-420 年） <a class="header-anchor" href="#_9-晋朝-265-年-420-年" aria-label="Permalink to &quot;9. 晋朝（265 年-420 年）&quot;">​</a></h4><ul><li><strong>西晋</strong>（265 年-316 年）：司马炎建立西晋，八王之乱。</li><li><strong>东晋</strong>（317 年-420 年）：司马睿重建晋朝，东晋十六国并存。</li></ul><h4 id="_10-南北朝-420-年-589-年" tabindex="-1">10. 南北朝（420 年-589 年） <a class="header-anchor" href="#_10-南北朝-420-年-589-年" aria-label="Permalink to &quot;10. 南北朝（420 年-589 年）&quot;">​</a></h4><ul><li><strong>南朝</strong>：宋、齐、梁、陈四朝更替。</li><li><strong>北朝</strong>：北魏、东魏、西魏、北齐、北周五朝更迭。</li><li><strong>重要事件</strong>：北魏孝文帝改革，促进民族融合。</li></ul><h4 id="_11-隋朝-581-年-618-年" tabindex="-1">11. 隋朝（581 年-618 年） <a class="header-anchor" href="#_11-隋朝-581-年-618-年" aria-label="Permalink to &quot;11. 隋朝（581 年-618 年）&quot;">​</a></h4><ul><li><strong>建立</strong>：杨坚建立隋朝，统一全国。</li><li><strong>重要事件</strong>：开通大运河；隋末农民起义，隋朝灭亡。</li></ul><h4 id="_12-唐朝-618-年-907-年" tabindex="-1">12. 唐朝（618 年-907 年） <a class="header-anchor" href="#_12-唐朝-618-年-907-年" aria-label="Permalink to &quot;12. 唐朝（618 年-907 年）&quot;">​</a></h4><ul><li><strong>建立</strong>：李渊建立唐朝，灭隋。</li><li><strong>重要事件</strong>：贞观之治、开元盛世；安史之乱；黄巢起义。</li></ul><h4 id="_13-五代十国-907-年-979-年" tabindex="-1">13. 五代十国（907 年-979 年） <a class="header-anchor" href="#_13-五代十国-907-年-979-年" aria-label="Permalink to &quot;13. 五代十国（907 年-979 年）&quot;">​</a></h4><ul><li><strong>五代</strong>：后梁、后唐、后晋、后汉、后周五个政权更替。</li><li><strong>十国</strong>：南方和巴蜀地区十个割据政权并存。</li></ul><h4 id="_14-宋朝-960-年-1279-年" tabindex="-1">14. 宋朝（960 年-1279 年） <a class="header-anchor" href="#_14-宋朝-960-年-1279-年" aria-label="Permalink to &quot;14. 宋朝（960 年-1279 年）&quot;">​</a></h4><ul><li><strong>北宋</strong>（960 年-1127 年）：赵匡胤建立北宋，王安石变法。</li><li><strong>南宋</strong>（1127 年-1279 年）：赵构建立南宋，岳飞抗金；崖山海战后南宋灭亡。</li></ul><h4 id="_15-辽、金、西夏-907-年—1279-年" tabindex="-1">15. 辽、金、西夏（907 年—1279 年） <a class="header-anchor" href="#_15-辽、金、西夏-907-年—1279-年" aria-label="Permalink to &quot;15. 辽、金、西夏（907 年—1279 年）&quot;">​</a></h4><ul><li><strong>辽</strong>：契丹族建立，与宋长期对抗。</li><li><strong>金</strong>：女真族建立，灭辽侵宋。</li><li><strong>西夏</strong>：党项族建立，与宋、辽、金形成三足鼎立。</li></ul><h4 id="_16-元朝-1271-年-1368-年" tabindex="-1">16. 元朝（1271 年-1368 年） <a class="header-anchor" href="#_16-元朝-1271-年-1368-年" aria-label="Permalink to &quot;16. 元朝（1271 年-1368 年）&quot;">​</a></h4><ul><li><strong>建立</strong>：忽必烈定国号为元，灭南宋，统一全国。</li><li><strong>重要事件</strong>：行省制度的确立；元末农民起义。</li></ul><h4 id="_17-明朝-1368-年-1644-年" tabindex="-1">17. 明朝（1368 年-1644 年） <a class="header-anchor" href="#_17-明朝-1368-年-1644-年" aria-label="Permalink to &quot;17. 明朝（1368 年-1644 年）&quot;">​</a></h4><ul><li><strong>建立</strong>：朱元璋建立明朝，定都应天府（后迁都顺天府）。</li><li><strong>重要事件</strong>：郑和下西洋；土木堡之变；明末农民战争。</li></ul><h4 id="_18-清朝-1644-年-1912-年" tabindex="-1">18. 清朝（1644 年-1912 年） <a class="header-anchor" href="#_18-清朝-1644-年-1912-年" aria-label="Permalink to &quot;18. 清朝（1644 年-1912 年）&quot;">​</a></h4><ul><li><strong>建立</strong>：皇太极改国号为清，入关建立全国性政权。</li><li><strong>重要事件</strong>：康乾盛世；鸦片战争；太平天国起义；甲午战争；辛亥革命推翻清朝统治。</li></ul>',38),e=[i];function n(s,h,u,d,g,_){return r(),l("div",null,e)}const q=a(o,[["render",n]]);export{b as __pageData,q as default};
